 "use strict";
 
jQuery(document).ready(function($){
	
		// browser window scroll (in pixels) after which the "back to top" link is shown
		var offset = 300,
		//browser window scroll (in pixels) after which the "back to top" link opacity is reduced
		offset_opacity = 1200,
		//duration of the top scrolling animation (in ms)
		scroll_top_duration = 700,
		//grab the "back to top" link
		$back_to_top = jQuery('.cd-top');

	//hide or show the "back to top" link
	jQuery(window).scroll(function(){
		( jQuery(this).scrollTop() > offset ) ? $back_to_top.addClass('cd-is-visible') : $back_to_top.removeClass('cd-is-visible cd-fade-out');
		if( jQuery(this).scrollTop() > offset_opacity ) { 
			$back_to_top.addClass('cd-fade-out');
		}
	});

	//smooth scroll to top
	$back_to_top.on('click', function(event){
		event.preventDefault();
		jQuery('body,html').animate({
			scrollTop: 0 ,
		 	}, scroll_top_duration
		);
	});

	//================================================= Page Easing end here ============= //
 
		  
                jQuery('.single_item_carousel').owlCarousel({
                    items: 3,
                    autoPlay: 3000,
                });
				
				 jQuery('.four_item_carousel').owlCarousel({
                    items: 4,
                    autoPlay: 3000,
                });
				
                
                jQuery(".one_item_carousel").owlCarousel({
                    	 singleItem:true, 
						autoPlay: true
                    });
                    
                });
		 		
				jQuery(".team_carousel").owlCarousel({
					singleItem:true,                 
					navigation:true,
					pagination:false
					});
            
     
	//================================================= OWL Slider end here ============= //
	
	 jQuery('.page-scroll a').bind('click', function(event) {
                var $anchor = jQuery(this);
                jQuery('html, body').stop().animate({
                            scrollTop: jQuery($anchor.attr('href')).offset().top
                }, 1500, 'easeInOutExpo');
                event.preventDefault();
           });
	
	